import React from 'react';
import loadable from '@loadable/component'
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
//import logo from './logo.svg';
import PropTypes from "prop-types";
import classNames from "classnames";
import { withStyles } from '@material-ui/core/styles';
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import Typography from "@material-ui/core/Typography";
import Badge from "@material-ui/core/Badge";
import Drawer from "@material-ui/core/Drawer";
import Hidden from '@material-ui/core/Hidden';
import Divider from "@material-ui/core/Divider";
import List from "@material-ui/core/List";
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import NotificationsIcon from '@material-ui/icons/Notifications';
import styles from './AppStyle';

import {mainListItems, secondaryListItems} from "./examples/dashboard/listItems";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";

import PhotoAlbumIcon from '@material-ui/icons/PhotoAlbum';
import CheckIcon from '@material-ui/icons/Check';
import DashboardIcon from "@material-ui/core/SvgIcon/SvgIcon";


const Album = loadable(() => import('./examples/album/Album'))
const Checkout = loadable(() => import('./examples/checkout/Checkout'))

const routes = [
  {
    path: "/ex/album",
    label: 'Album',
    component: Album,
    icon: PhotoAlbumIcon,
  },
  {
    path: "/ex/checkout",
    label: 'Checkout',
    component: Checkout,
    icon: CheckIcon,
  },
];


class AnyTag extends React.Component {
  render() {
    const TagName = this.props.tag;
    return <TagName />
  }
}

const sideMenuItems = (
    <div>
      {routes.map( route => (
        <Link to={route.path}>
          <ListItem button>
            <ListItemIcon>
              <AnyTag tag={route.icon}/>
            </ListItemIcon>
            <ListItemText primary={route.label} />
          </ListItem>
        </Link>
      ))}
    </div>
);


class App extends React.Component {
  state = {
    open: true,
  };

  handleDrawerOpen = () => {
    this.setState({ open: true });
  };

  handleDrawerClose = () => {
    this.setState({ open: false });
  };

  handleDrawerToggle = () => {
    this.setState(state => ({ open: !state.open }));
  };

  render() {
    const { classes, theme } = this.props;

    return (
      <Router>
        <CssBaseline />
        <AppBar
            position="absolute"
            className={classNames(classes.appBar, this.state.open && classes.appBarShift)}
        >
          <Toolbar disableGutters={!this.state.open} className={classes.toolbar}>
            <IconButton
                color="inherit"
                aria-label="Open drawer"
                onClick={this.handleDrawerOpen}
                className={classNames(
                    classes.menuButton,
                    this.state.open && classes.menuButtonHidden,
                )}
            >
              <MenuIcon />
            </IconButton>
            <Typography
                component="h1"
                variant="h6"
                color="inherit"
                noWrap
                className={classes.title}
            >
              Dashboard
            </Typography>
            <IconButton color="inherit">
              <Badge badgeContent={4} color="secondary">
                <NotificationsIcon />
              </Badge>
            </IconButton>
          </Toolbar>
        </AppBar>
        <nav className={classes.drawer}>
          {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
          <Hidden smUp implementation="css">
            <Drawer
                container={this.props.container}
                anchor="left"
                onClose={this.handleDrawerToggle}
                variant="temporary"
                //variant="permanent"
                classes={{
                  paper: classNames(classes.drawerPaper, !this.state.open && classes.drawerPaperClose),
                }}
                open={this.state.open}
            >
              <div className={classes.toolbarIcon}>
                <IconButton onClick={this.handleDrawerClose}>
                  <ChevronLeftIcon />
                </IconButton>
              </div>
              <Divider />
              <List>{sideMenuItems  }</List>
              <Divider />
              <List>{secondaryListItems}</List>
            </Drawer>
          </Hidden>
        </nav>
        <main className={classes.content}>
          <div className={classes.appBarSpacer} />
          {routes.map((route, index) => (
              // You can render a <Route> in as many places
              // as you want in your app. It will render along
              // with any other <Route>s that also match the URL.
              // So, a sidebar or breadcrumbs or anything else
              // that requires you to render multiple things
              // in multiple places at the same URL is nothing
              // more than multiple <Route>s.
              <Route
                  key={index}
                  path={route.path}
                  component={route.component}
              />
          ))}
        </main>
      </Router>
    )
  }
}

App.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired,
};

export default withStyles(styles)(App);
